package ch03.lecture.p2.binary;

public class C09Assignment {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=3;
		int j=5;
		
		int k = 1;
		System.out.println(k);
		
		k+=j;
		System.out.println(k);
		
		k-=i;
		System.out.println(k);
		
		k*=i;
		System.out.println(k);
		
		k/=j;
		System.out.println(k);
		
		k%=i;
		System.out.println(k);
	}

}
